var zPlayer=document.getElementById("player");function zMute(){zPlayer.muted=!zPlayer.muted}function zPlay(){zPlayer.play()}function zPause(){zPlayer.pause()}function zVolumeUp(){zPlayer.volume+=0.1}function zVolumeDown(){zPlayer.volume-=0.1}function zTimeUpdate(){document.getElementById("currenttime").innerHTML=convertTime(Math.floor(zPlayer.currentTime));document.getElementById("totaltime").innerHTML=convertTime(Math.floor(zPlayer.duration));document.getElementById("time").value=Math.floor(zPlayer.currentTime);document.getElementById("time").max=Math.floor(zPlayer.duration)}function convertTime(c){var b=Math.floor(c/3600);var a=Math.floor((c%3600)/60);c=c%60;if(c<10){c="0"+c}if(a<10){a="0"+a}if(b!=0){return b+":"+a+":"+c}else{return a+":"+c}};